import './App.css';
import react, {Component, useState, useEffect } from 'react';
import Navigation from './components/Navigation/Navigation.js';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Signin from './components/Signin/Signin';
import Register from './components/Register/Register';
import Particles from 'react-particles-js';

/*
  Particles API used for the background
*/
const particlesOptions = {
particles: {                 
  number: {
    value: 30,
    density:{
      enable:true,
      value_area:185,
      }
    }
  }
}                

const initialState = () =>{
  setInput('');
  setImageUrl('');
  setBox([{}]);
  setRoute('signin');
  setIsSignedIn(false);
  setUser({
                                    id: '',
                                    name: '',
                                    email: '',
                                    password: '',
                                    entries: '',
                                    joined: '',
                                  });
}
/*const initialState = {
      input: '',
      imageUrl: '',
      box:[{}],//turned into an array, to read more than one face
      route: 'signin',//where we are an the page
      isSignedIn: false,
      user:{
        id: '',
        name: '',
        email: '',
        password: '',
        entries: '',
        joined: '',
      }
    }*/
const App = () => {

  const [input, setInput] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [box, setBox] = useState([{}]);
  const [route, setRoute] = useState('signin');
  const [isSignedIn, setIsSignedIn] = useState(false);
  const [user, setUser] = useState({
                                    id: '',
                                    name: '',
                                    email: '',
                                    password: '',
                                    entries: '',
                                    joined: '',
                                  });
  /*constructor(){
    super();
    this.state = initialState;
  }*/




/*
  Function that calculates face location based on coordinates given by CLARIFAI API
  Returns an array with all the coordinates
*/
  calculateFaceLocation = (data) =>{
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    const faceCoordinates = data.outputs[0].data.regions;
    let facesDetected = [];

    faceCoordinates.forEach(face=>{
      const clarifaiFace = face.region_info.bounding_box
      const coordinates = {
        leftCol: (clarifaiFace.left_col) * width,
        topRow: (clarifaiFace.top_row) * height,
        rightCol: width - (clarifaiFace.right_col * width),
        bottomRow: height - (clarifaiFace.bottom_row * height)
      }
      facesDetected.push(coordinates);
    })

    return facesDetected;
  }
  /*
    displayFaceBox takes locations of the box, and sets the 'box' state
  */
  displayFaceBox = (boxLocations) =>{
        setBox(boxLocations);
  }
  /*
    when the user pastes the link of the image in the input box, changes the 'input' state
  */
  onInputChange = (event) =>{
    setInput(event.target.value)
  }
  /*
    when the user submits the image link, this function send a request to the server.
    the server handles the CLARIFAI API call, to detect all the faces.
    The server send a response with all the detected faces, and calculateFaceLocation does the rest.
    this response brings us in another fetch to the backend, to update the entries
  */
  onSubmit = () =>{
    setImageUrl(input);
    fetch ("https://ancient-forest-08678.herokuapp.com/imageurl", {
          method : 'post',
          headers: {'Content-Type': 'application/json'},
          body: JSON.stringify({
            input: input
            })
          })
          .then (response => response.json())
    .then(response=>{
        if (response){
          fetch ("https://ancient-forest-08678.herokuapp.com/image", {
          method : 'put',
          headers: {'Content-Type': 'application/json'},
          body: JSON.stringify({
            id: user.id//send the id to the server. it will update that user's count
            })
          })
          .then(response => response.json())
          .then(count =>{
              setUser(Object.assign(user, {entries: count}));//use object assign (the object to update, {the element to add})
          }).catch(console.log);

          this.displayFaceBox(this.calculateFaceLocation(response));
        }
      })
    .catch(e=>console.log('error',e))
  }

  onRouteChange = (route) =>{
    if (route === 'signin'){
      initialState();
    }else if (route === 'home'){
      setIsSignedIn({isSignedIn: true})
    }
    setRoute({route: route});
  }

  loadUser = (user) =>{
    setUser({user:{
        id: user.id,
        name: user.name,
        email: user.email,
        password: user.password,
        entries: user.entries,
        joined: user.joined,
    }})
  }
    let section;

    if (route === 'signin'){
          section = (<Signin loadUser={this.loadUser}onRouteChange={this.onRouteChange}/>);
      }else if(route === 'register'){
          section = (<Register loadUser = {this.loadUser} onRouteChange = {this.onRouteChange}/>);
      }else{
          section = (
          <div>     
            <Logo /> 
            <Rank userName = {user.name} entries = {user.entries}/>
            <ImageLinkForm onInputChange={this.onInputChange} onSubmit={this.onSubmit}/>
            <FaceRecognition boxes = {box} imageUrl={imageUrl}/>
          </div>
        )
      }

    return (
      <div className="App">
        <Navigation isSignedIn={isSignedIn} onRouteChange = {this.onRouteChange} />
        <Particles params={particlesOptions} className='particles'/>
        {section}
      </div>
    );

}

export default App;
