import react, {useState} from 'react';
import '../ImageLinkForm.css';
import Modal from 'react-modal' ;

const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};
 
// Make sure to bind modal to your appElement (http://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement(document.getElementById('root'));

const DeleteUser = ({user}) =>{

	const onClickDeleteUser = () =>{
		console.log(user.id)
		fetch (`https://ancient-forest-08678.herokuapp.com/modify/delete`, {//fetch will automatically use GET method, but we want PUT
				method : 'delete',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
					id: user.id
				})
			})
	        .then(response => response.json())
	        .then(modifiedElement=> {
	        	window.location.reload(true);
	        })
	        .catch(error =>console.log(error));
	}
	return (
		<div className="fl w-100 w-50-ns pa2">
			<button className= 'w-100 grow f4 link ph3 pv2 dib white bg-dark-red'
					onClick={() =>onClickDeleteUser}>
					Delete User
			</button>

		</div>
		)

}

export default DeleteUser;
