import react from 'react';
import './ImageLinkForm.css';

 const ImageLinkForm = ({onInputChange, onSubmit}) =>{//DESTRUCTURING

	return (
		 <div>
		 	<p className='f3'>
		 		{'This thing will detect faces in your pictures. Try it'}
		 	</p>
		 	<div className='form center pa4 br3 shadow-5 flex flex-wrap'>{/*Created center css class, adding flex, justifying center*/}
		 		<div>
			 		<input type='text' 
			 			   className='f4 pa2 w-70 center' 
			 			   onChange={onInputChange}
			 			   placeholder = 'Insert your image link here...'/>
			 		<button className='w-30 grow f4 link ph3 pv2 dib white bg-dark-blue' 
			 		        onClick={onSubmit}>Detect</button> 
  				</div>
		 		<div class="tc">
    				<p className= 'f5 tc'>InsertLink</p>
  				</div>
		 	</div>
		 </div>
	);
}

export default ImageLinkForm;
