import './App.css';
import react, {Component} from 'react';
import Navigation from './components/Navigation/Navigation.js';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai'

const app = new Clarifai.App({
  apiKey : 'f9e837ab0386499e9583b19c25f83b74'
});
/*
line_linked: {
                    shadow: {
                      enable: true,
                      color: "#3CA9D1",
                      blur: 5,
                    }
                  }

*/
const particlesOptions = {
particles: {                 
  number: {
    value: 30,
    density:{
      enable:true,
      value_area:185,
      }
    }
  }
}                


class App extends Component {

  constructor(){
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box:{
      }
    }
  }

  calculateFaceLocation = (data) =>{
    console.log(data)
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    console.log(width, height);
    return{
      leftCol: clarifaiFace.left_col * width, //197
      topRow: clarifaiFace.top_row * height,//59
      rightCol: width - (clarifaiFace.right_col * width),//195.7
      bottomRow: height - (clarifaiFace.bottom_row * height),//274
    }
  }
  displayFaceBox = (box) =>{
    console.log(box)
    this.setState ({box: box});
  }
  onInputChange = (event) =>{
    this.setState({input: event.target.value})
  }

  onSubmit = () =>{
    this.setState ({imageUrl : this.state.input})
    app.models.predict(Clarifai.FACE_DETECT_MODEL,this.state.input)
    .then(response=>{
      return this.displayFaceBox(this.calculateFaceLocation(response))
    })
    .catch(e=>console.log('error',e))
  }
  render(){
    return (
      <div className="App">
        <Particles params={particlesOptions} className='particles'/>{/*z-index:-1 set a background*/}
        <Navigation />     
        <Logo /> 
        <Rank />
        <ImageLinkForm onInputChange={this.onInputChange} onSubmit={this.onSubmit}/>
        <FaceRecognition box = {this.state.box} imageUrl={this.state.imageUrl}/>
      </div>
    );
  }  
}

export default App;
